# weight_converter()


class Converter:

    def __init__(self):
        self.unit = input("Choose between (K)g/(L)bs: ").upper()
        self.weight = int(input("Enter your weight: "))

    def weight_converter_kg(self):
        if self.unit == "K":
            converted_weight_kg = self.weight // 0.45
            print(f"Your weight is {converted_weight_kg} lbs")
            return converted_weight_kg

        else:
            print("Invalid input")
            return None

    def weight_converter_lbs(self):
        if self.unit == "L":
            converted_weight_lbs = self.weight * 0.45
            print(f"Your weight is {converted_weight_lbs} kg")
            return converted_weight_lbs

        else:
            print("Invalid input")
            return None


def main():
    user1 = Converter()
    user1.weight_converter_kg()


if __name__ == "__main__":
    main()
